import React, { useState } from "react";
import ProfileList from "./ProfileList";
import ProfileForm from "./ProfileForm";

function Account() {
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [profilePic, setProfilePic] = useState("");
  const [bio, setBio] = useState("");

  const [editRecordId, setEditRecordId] = useState(0);

  const [records, setRecords] = useState([
    {
      id: 1,
      name: "femi",
      phone: "0918819919",
      email: "emmafemi0@gmail",
      profilePic:
        "https://pbs.twimg.com/profile_images/1609154394229964800/gHa_TDTd_400x400.jpg",
      bio: "Javascript is good",
    },
  ]);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (editRecordId) {
      const updatedRecords = records.map((record) =>
        record.id === editRecordId
          ? {
              id: record.id,
              name,
              phone,
              email,
              profilePic,
              bio,
            }
          : record
      );
      setRecords(updatedRecords);
      setEditRecordId(0);
    } else {
      const newRecord = {
        id: Date.now(),
        name,
        phone,
        email,
        profilePic,
        bio,
      };
      setRecords([...records, newRecord]);
    }

    // Reset form fields
    setName("");
    setPhone("");
    setEmail("");
    setProfilePic("");
    setBio("");
  };

  const handleDelete = (id) => {
    const updatedRecords = records.filter((record) => record.id !== id);
    setRecords(updatedRecords);
  };

  const handleEdit = (id) => {
    const recordToEdit = records.find((record) => record.id === id);
    if (recordToEdit) {
      setName(recordToEdit.name);
      setPhone(recordToEdit.phone);
      setEmail(recordToEdit.email);
      setProfilePic(recordToEdit.profilePic);
      setBio(recordToEdit.bio);
      setEditRecordId(id);
    }
  };

  return (
    <div className="mt-20 mx-auto w-[1300px] gap-20 flex-col lg:flex lg:flex-row border-4 border-red-900">
      <ProfileList
        records={records}
        onDelete={handleDelete}
        onEdit={handleEdit}
      />
      <ProfileForm
        handleSubmit={handleSubmit}
        editRecordId={editRecordId}
        name={name}
        phone={phone}
        email={email}
        profilePic={profilePic}
        bio={bio}
        handleChange={(e) => {
          const { name, value } = e.target;
          switch (name) {
            case "name":
              setName(value);
              break;
            case "phone":
              setPhone(value);
              break;
            case "email":
              setEmail(value);
              break;
            case "profilePic":
              setProfilePic(value);
              break;
            case "bio":
              setBio(value);
              break;
            default:
              break;
          }
        }}
      />
    </div>
  );
}

export default Account;
