import React, { useState } from "react";
import InputForm from "./InputForm";
import ProfileForm from "./ProfileForm";
import ProfileList from "./ProfileList";

function Account() {
  const [value, setValue] = useState({
    name: "",
    phone: "",
    email: "",
    profilePic: "",
    bio: "",
  });
  const [editRecords, setEditRecords] = useState(0);

  const [records, setRecords] = useState([
    {
      id: 1,
      name: "femi",
      phone: "0918819919",
      email: "emmafemi0@gmail",
      profilePic:
        "https://pbs.twimg.com/profile_images/1609154394229964800/gHa_TDTd_400x400.jpg",
      bio: "Javascript is good",
    },
  ]);

  const handleChange = (e) => {
    setValue({ ...value, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (editRecords) {
      const editRecord = records.find((i) => i.id === editRecords);
      const updatedRecords = records.map((t) =>
        t.id === editRecord.id
          ? {
              id: t.id,
              name: value.name,
              phone: value.phone,
              email: value.email,
              profilePic: value.profilePic,
              bio: value.bio,
            }
          : t
      );

      setRecords(updatedRecords);
      setEditRecords(0);
      setValue({
        name: "",
        email: "",
        profilePic: "",
        phone: "",
        bio: "",
      });

      return;
    }

    if (
      value.name !== "" &&
      value.phone !== "" &&
      value.email !== "" &&
      value.profilePic !== "" &&
      value.bio !== ""
    ) {
      const id = Date.now();

      const payload = {
        id: id,
        name: value.name,
        phone: value.phone,
        email: value.email,
        profilePic: value.profilePic,
        bio: value.bio,
      };

      setRecords([...records, payload]);
      setValue({
        name: "",
        email: "",
        profilePic: "",
        phone: "",
        bio: "",
      });
    }
  };

  const handleDelete = (id) => {
    setRecords(records.filter((record) => record.id !== id));
  };

  const handleEdit = (id) => {
    const editRecord = records.find((r) => r.id === id);
    setValue({
      name: editRecord.name,
      email: editRecord.email,
      phone: editRecord.phone,
      profilePic: editRecord.profilePic,
      bio: editRecord.bio,
    });
    setEditRecords(id);
  };

  return (
    <div className="mt-28 mx-auto max-w-screen-xl">
      <h1 className="bg-blue-50 text-gray-500 font-semibold text-3xl p-2 mb-4">
        React CRUD Application
      </h1>
      <div className="gap-16 flex-col lg:flex lg:flex-row">
        <ProfileList
          records={records}
          onDelete={handleDelete}
          onEdit={handleEdit}
        />
        <ProfileForm
          handleSubmit={handleSubmit}
          editRecords={editRecords}
          handleChange={handleChange}
          name={value.name}
          email={value.email}
          phone={value.phone}
          profilePic={value.profilePic}
          bio={value.bio}
        />
      </div>
    </div>
  );
}

export default Account;
