import React, { useState } from "react";
import { Link, useHistory } from "react-router-dom";
import "./CreateAccount.css";

const CreateAccount = () => {
  const history = useHistory();

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [profilePic, setProfilePic] = useState(null);
  const [profilePics, setProfilePics] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validate form fields
    if (!name || !email || !password) {
      console.log("Please fill in all the required fields.");
      return;
    }

    // Perform the necessary actions to create the account
    const userData = {
      name: name,
      email: email,
      password: password,
      profilePic: profilePic,
    };

    // Send the userData to the server or perform any necessary actions
    console.log("Creating account with the following data:", userData);

    // Clear the form fields
    setName("");
    setEmail("");
    setPassword("");
    setProfilePic(null);
    setProfilePics([]);

    // Redirect to login page
    history.push("/login");
  };

  const handleProfilePicChange = (e) => {
    const file = e.target.files[0];
    setProfilePic(URL.createObjectURL(file));
  };

  const handleAddProfilePic = () => {
    if (profilePic) {
      setProfilePics((prevPics) => [...prevPics, profilePic]);
      setProfilePic(null);
    }
  };

  return (
    <div className="create-account">
      <h2>Create Account</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-row">
          <label htmlFor="name">Name</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </div>
        <div className="form-row">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="form-row">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div className="form-row">
          <label htmlFor="profile-pic">Profile Picture</label>
          <input
            type="file"
            id="profile-pic"
            accept="image/*"
            onChange={handleProfilePicChange}
          />
        </div>
        <div className="profile-pics">
          {profilePics.map((pic, index) => (
            <img key={index} src={pic} alt={`Profile ${index + 1}`} />
          ))}
          {profilePic && <img src={profilePic} alt="New Profile" />}
        </div>
        <button type="button" onClick={handleAddProfilePic}>
          Add Profile Picture
        </button>
        <button type="submit">Create Account</button>
      </form>
      <p>
        Already have an account? <Link to="/login">Log in</Link>
      </p>
    </div>
  );
};

export default CreateAccount;
