import React, { useState, useEffect } from "react";
import data from "../data";
import { useNavigate, useParams } from "react-router";
import { BsFillCaretLeftFill } from "react-icons/bs";

function Profile() {
  const idToBe = useParams().id - 1;
  const [userData, setUserData] = useState(null);
  const [editing, setEditing] = useState(false);
  const [name, setName] = useState("");
  const [bodyType, setBodyType] = useState("");
  const [interests, setInterests] = useState("");

  const history = useNavigate();

  useEffect(() => {
    setUserData(data.users[idToBe]);
  }, [idToBe]);

  useEffect(() => {
    if (userData) {
      setName(userData.name);
      setBodyType(userData.bodyType);
      setInterests(userData.interests);
    }
  }, [userData]);

  const goBack = (e) => {
    e.preventDefault();
    history(-1);
  };

  const handleSave = (e) => {
    e.preventDefault();
    const updatedUserData = {
      ...userData,
      name,
      bodyType,
      interests,
    };
    setUserData(updatedUserData);
    setEditing(false);
  };

  if (!userData) {
    return <div>Loading...</div>; // You can replace this with a loading indicator
  }

  return (
    <>
      <article className="card">
        <div
          onClick={(e) => {
            e.preventDefault();
            goBack(e);
          }}
          className="icon-wrapper"
        >
          <BsFillCaretLeftFill />
        </div>
        <img src={userData.photos[0]} alt={userData.name} />
        {editing ? (
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        ) : (
          <h4>{name}</h4>
        )}
        <h5>
          age: {userData.age} height: {userData.height} cm
        </h5>
        {editing ? (
          <input
            type="text"
            value={bodyType}
            onChange={(e) => setBodyType(e.target.value)}
          />
        ) : (
          <h4>{bodyType}</h4>
        )}
        {editing ? (
          <input
            type="text"
            value={interests}
            onChange={(e) => setInterests(e.target.value)}
          />
        ) : (
          <h4>
            {interests[0]}, {interests[1]}, {interests[2]}
          </h4>
        )}

        {!editing ? (
          <button className="btn" onClick={() => setEditing(true)}>
            Edit Profile
          </button>
        ) : (
          <button className="btn" onClick={handleSave}>
            Save Profile
          </button>
        )}
      </article>
    </>
  );
}

export default Profile;
