import React, { useState } from 'react';
import "./App.css"
// import { BrowserRouter, Routes, Route } from "react-router-dom";
// import Error from './components/Errors';
// import Login from './components/Login';
// import Signup from './components/SignUp';
// import CreateAccount from './components/CreateAccount';
// import MainPage from './components/MainPage'
// import Messages from './components/Messages'
// import Profile from './components/Profile';
// import NavBar from './components/NavBar';
// import Logout from './components/Logout';
import ProfileForm from "./components/ProfileForm";
import ProfileList from "./components/ProfileList";

function App() {
  const [value, setValue] = useState({
    name: "",
    phone: "",
    email: "",
    profilePic: "",
    bio: "",
  });
  const [editRecords, setEditRecords] = useState(0);

  const [records, setRecords] = useState([
    {
      id: 1,
      name: "jason",
      phone: "0918819919",
      email: "kimata@gmail",
      profilePic:
        "https://pbs.twimg.com/profile_images/1609154394229964800/gHa_TDTd_400x400.jpg",
      bio: "Javascript is good",
    },
  ]);

  const handleChange = (e) => {
    setValue({ ...value, [e.target.name]: [e.target.value] });
  };

  const handleSubmit = (e) => {
    console.log("it has entered the form....");
    e.preventDefault();
    // const id = crypto.randomUUID();

    if (editRecords) {
      const editRecord = records.find((i) => i.id === editRecords);
      const updatedTodos = records.map((t) =>
        t.id === editRecord.id
          ? (t = {
              id: t.id,
              name: value.name,
              phone: value.phone,
              email: value.email,
              profilePic: value.profilePic,
              bio: value.bio,
            })
          : {
              id: t.id,
              name: t.name,
              email: t.email,
              profilePic: t.profilePic,
              phone: t.phone,
              bio: t.bio,
            }
      );
      console.log(editRecords);
      console.log("UpdatedTodos", updatedTodos);
      setRecords(updatedTodos);
      setEditRecords(0);
      setValue({
        name: "",
        email: "",
        profilePic: "",
        phone: "",
        bio: "",
      });

      return;
    }

    if (
      (value.name !== "",
      value.phone !== "",
      value.email !== "",
      value.profilePic !== "",
      value.bio !== "")
    ) {
      // const id = Math.floor(Math.random() * 10000) + 1;
      const id = Date.now();

      const payload = {
        id: id,
        name: value.name,
        phone: value.phone,
        email: value.email,
        profilePic: value.profilePic,
        bio: value.bio,
      };

      setRecords([...records, payload]);
      // console.log("payload from form", payload);
      setValue({
        name: "",
        email: "",
        profilePic: "",
        phone: "",
        bio: "",
      });
    }
  };

  const handleDelete = (id) => {
    setRecords(records.filter((record) => record.id !== id));
  };

  const handleEdit = (id) => {
    const editRecord = records.find((r) => r.id === id);
    setValue({
      name: editRecord.name,
      email: editRecord.email,
      phone: editRecord.phone,
      profilePic: editRecord.profilePic,
      bio: editRecord.bio,
    });
    setEditRecords(id);
    console.log(editRecord);
  };

  return (
    <div className="mt-28 mx-auto max-w-screen-xl">
      <h1 className="bg-blue-50 text-gray-500 font-semibold text-3xl p-2 mb-4">
        React Crud Application
      </h1>
      <div className="gap-16 flex-col lg:flex lg:flex-row">
        <ProfileList
          records={records}
          onDelete={handleDelete}
          onEdit={handleEdit}
        />
        <ProfileForm
          handleSubmit={handleSubmit}
          editRecords={editRecords}
          handleChange={handleChange}
          name={value.name}
          email={value.email}
          phone={value.phone}
          profilePic={value.profilePic}
          bio={value.bio}
        />
      </div>
    </div>
  );
}

export default App;
