import React, { useState } from "react";
import data from "../data";
import { useNavigate, useParams } from "react-router";
import { BsFillCaretLeftFill } from "react-icons/bs";

function Profile() {
  const idToBe = useParams().id - 1;
  const [userData, setUserData] = useState(data.users[idToBe]);
  const [editing, setEditing] = useState(false);
  const [name, setName] = useState(userData.name);
  const [age, setAge] = useState(userData.age);
  const [height, setHeight] = useState(userData.height);
  const [bodyType, setBodyType] = useState(userData.bodyType);
  const [interests, setInterests] = useState(userData.interests);

  const history = useNavigate();

  const goBack = (e) => {
    e.preventDefault();
    history(-1);
  };

  const handleSave = (e) => {
    e.preventDefault();
    const updatedUserData = {
      ...userData,
      name,
      age,
      height,
      bodyType,
      interests,
    };
    setUserData(updatedUserData);
    setEditing(false);
  };

  return (
    <>
      <article className="card">
        <div
          onClick={(e) => {
            e.preventDefault();
            goBack(e);
          }}
          className="icon-wrapper"
        >
          <BsFillCaretLeftFill />
        </div>
        <img src={userData.photos[0]} alt={userData.name} />
        {editing ? (
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        ) : (
          <h4>{name}</h4>
        )}
        <h5>
          age: {age} height: {height} cm
        </h5>
        {editing ? (
          <input
            type="text"
            value={bodyType}
            onChange={(e) => setBodyType(e.target.value)}
          />
        ) : (
          <h4>{bodyType}</h4>
        )}
        {editing ? (
          <input
            type="text"
            value={interests}
            onChange={(e) => setInterests(e.target.value)}
          />
        ) : (
          <h4>
            {interests[0]}, {interests[1]}, {interests[2]}
          </h4>
        )}

        {!editing ? (
          <button className="btn" onClick={() => setEditing(true)}>
            Edit Profile
          </button>
        ) : (
          <button className="btn" onClick={handleSave}>
            Save Profile
          </button>
        )}
      </article>
    </>
  );
}

export default Profile;
